script:
let button = this.button;
if (this.action && this.action.name) {
    button=this.action.name;
}

const emdb=API.cache('emdb');;
const MolecularFormula = API.cache('MolecularFormula');
const IsotopicDistribution=MolecularFormula.IsotopicDistribution;

const massData = API.cache('massData');



switch (button) {
    case 'loadSpectrum':
        loadSpectrum(massData);
        this.hideButton('calibrateAndRecalculate');
        return;
    case 'calculate':
        loadSpectrum(massData);
        break;
    case 'estimate':
        let numberPossibilities=estimate(this.action.value);
        if (numberPossibilities !== undefined) {
          API.createData('numberPossibilities',numberPossibilities);  
        }
        return;
    case 'calibrateAndRecalculate':
        const shiftFunction=API.cache('shiftFunction');
        if (! shiftFunction) {
            API.showNotification('Unable to recalibrate the spectrum, no function available.')
            return;
        }
        let x=massData.x.map( x => x-shiftFunction(x) );
        loadSpectrum({
            x,
            y: massData.y
        })

        break;
    case 'UpdateAnalyseButtons':
        console.log('UpdateAnalyseButtons')
        if (API.cache('shiftFunction')) {
            this.showButton('calibrateAndRecalculate');
        } else {
            this.hideButton('calibrateAndRecalculate');
        }
        return;
}


const preferences=API.getData('preferences').resurrect();
const kind=preferences.sequenceKind;

let filter=JSON.parse(JSON.stringify(preferences.filter));
if (emdb.experimentalSpectrum) {
    let peaks=emdb.experimentalSpectrum.getPeaks({threshold: preferences.mass.threshold / 100});
    filter.precision=preferences.mass.precision;
    if (preferences.mass.monoisotopic) {
        filter.targetMasses=peaks.map( peak => peak.x );
        filter.targetIntensities=peaks.map( peak => peak.y );
    }
}

let similarity = JSON.parse(JSON.stringify(preferences.mass.similarity));

let fragments=[];
let database='';

try {
    switch (kind) {
        case 'peptidic':
            emdb.fromPeptidicSequence(preferences.peptidic.sequence, {
                ionizations: preferences.ionizations,
                mfsArray: preferences.mfsArray,
                digestion: preferences.peptidic.digestion,
                fragmentation: preferences.peptidic.fragmentation,
                protonationPH: preferences.peptidic.protonationPH,
                protonation: preferences.peptidic.protonation,
                allowNeutralLoss: preferences.peptidic.allowNeutralLoss,
                filter
            });
            database='peptidic';
            break;
        case 'nucleic':
            // temporary code to force remove ab fragmentation
            preferences.nucleic.fragmentation.ab=undefined;
            emdb.fromNucleicSequence(preferences.nucleic.sequence, {
                ionizations:preferences.ionizations,
                mfsArray:preferences.mfsArray,
                fragmentation: preferences.nucleic.fragmentation,
                info: preferences.nucleic.info,
                onStep: (value) => API.doAction('setCurrentProgress', value),
                filter
            });
            database='nucleic';
            break;
        case 'mixture':
            emdb.fromRange(preferences.mfRange, {
                ionizations:preferences.ionizations,
                mfsArray:preferences.mfsArray,
                onStep: (value) => API.doAction('setCurrentProgress', value),
                filter
            });
            database='generated';
            break;
    }
} catch (e) {
    UI.showNotification(e.toString());
}

if (! database) return;
fragments=emdb.get(database);

if (preferences.mass.minSimilarity>0 && emdb.experimentalSpectrum) {
    if (similarity.widthFunction) {
        similarity.widthFunction =
            'var massError = '+preferences.mass.precision+' * mass * 1e-6;\n' + 
            similarity.widthFunction;
    }

    fragments = emdb.searchSimilarity({
        flatten: true,
        minSimilarity: preferences.mass.minSimilarity / 100,
        filter,
        similarity
    });
}

API.createData('fragments', fragments);

let totalQuantity=0;
const sameEMcounter={};
fragments.forEach( fragment => {
    let em=Number(fragment.em);
    if (! sameEMcounter[em]) {
        sameEMcounter[em]=0;
        if (fragment.ms && fragment.ms.similarity) totalQuantity+=fragment.ms.similarity.quantity;
    }
    sameEMcounter[em]++;
});

const sameMSEMcounter={};
const uniqueMSEMFragments=[];
fragments.forEach( fragment => {
    let em=Number(fragment.ms.em);
    if (! sameMSEMcounter[em]) {
        sameMSEMcounter[em]=true;
        uniqueMSEMFragments.push(fragment);
    }
});


fragments.forEach( fragment => {
    fragment.group=sameEMcounter[fragment.em];
});

// add highlight
fragments.forEach( fragment => {
    fragment._highlight=fragment.em || fragment.comment || Math.random();
})


API.createData('totalQuantity',totalQuantity);



let chargedFragments=uniqueMSEMFragments.filter(fragment => Number(fragment.ms.charge)!==0);
chargedFragments.sort( (a,b) => b.ms.similarity.quantity - a.ms.similarity.quantity);

if (chargedFragments.length>100) {
    UI.showNotification('Limiting recontructed spectrum to 100 (out of '+chargedFragments.length+')','warning')
    chargedFragments=chargedFragments.slice(0,100);
}

let isotopicDistribution=new IsotopicDistribution(chargedFragments);
var data=isotopicDistribution.getXY({maxValue:false});

var fullSpectrum={
    type:"chart",
    value:{
        title:"Mass spectrum",
        data
    }
};

API.createData("fullSpectrum",fullSpectrum);



window.setTimeout( () => {
    switch (button) {
        case 'calculate':
            API.doAction('calculateXShiftFunction');
            API.createData('rescaledMass','');
            break;
        case 'calibrateAndRecalculate':
            API.doAction('calculateXNewShiftFunction');
            API.createData('rescaledMass','Masses were recalibrated');
            break;
    }

})

function loadSpectrum(massData) {
    if (! massData) return;
    emdb.setExperimentalSpectrum(massData);
    emdb.experimentalSpectrum.scaleY(100);
    let data=emdb.experimentalSpectrum.data;

    let peaksWidth;
    try {
        peaksWidth=emdb.experimentalSpectrum.peaksWidth();
        API.cache('peaksWidth', peaksWidth);
    } catch (e) {
        console.log('Can not calculate peak width');
    }


    if (peaksWidth && peaksWidth.score.r2>0) {
        API.createData('peaksWidths', peaksWidth.widths);
        API.createData('peaksWidthFit', peaksWidth.fit);
        API.createData('peaksWidthR2', peaksWidth.score.r2);
        API.createData('peaksWidthTex', peaksWidth.tex);
    } else {
        UI.showNotification('Could not calculate peaks width','warning');
        API.createData('peaksWidths', undefined);
        API.createData('peaksWidthFit', undefined);
        API.createData('peaksWidthR2', undefined);
        API.createData('peaksWidthTex', undefined);
    }

    API.createData('peaks', JSON.parse(JSON.stringify(emdb.experimentalSpectrum.getPeaks())) );
    API.createData('experimentalChart', data);
    API.createData('experimentalIsContinuous', emdb.experimentalSpectrum.isContinuous());
    API.doAction('autoPeakWidth');

    window.setTimeout( () => API.doAction('fullOut','xy'), 1);
}

function estimate(preferences) {
    console.log({preferences});
    switch (preferences.sequenceKind) {
        case 'peptidic':
            console.log('PEPTIDIC')
            return emdb.fromPeptidicSequence(preferences.peptidic.sequence, {
                ionizations: preferences.ionizations,
                mfsArray: preferences.mfsArray,
                digestion: preferences.peptidic.digestion,
                fragmentation: preferences.peptidic.fragmentation,
                protonationPH: preferences.peptidic.protonationPH,
                protonation: preferences.peptidic.protonation,
                allowNeutralLoss: preferences.peptidic.allowNeutralLoss,
                limit: 10000,
                estimate: true,
            });
        case 'nucleic':
            return emdb.fromNucleicSequence(preferences.nucleic.sequence, {
                ionizations:preferences.ionizations,
                mfsArray:preferences.mfsArray,
                fragmentation: preferences.nucleic.fragmentation,
                info: preferences.nucleic.info,
                estimate: true,
            });
        case 'polymer':
            return emdb.fromRange(preferences.mfRange, {
                ionizations:preferences.ionizations,
                mfsArray:preferences.mfsArray,
                estimate: true,
            });
    }
}

console.log('Loading experimental spectrum');

API.createData('fragments', []);
API.createData('kindAnnotations', []);
API.createData('annotations', []);
API.createData('fullSpectrum', []);
API.createData('widthAnnotation', []);
API.createData('totalQuantity', []);

let variable=API.getData('experimentalRow');

var experimentalChart = await Util.getChartFromMass(variable, {
    name: 'experimental mass'
});
const massData=experimentalChart.data[0];
API.cache('massData', massData);
API.cache('shiftFunction', undefined);
API.cache('previousZoom', {});

API.doAction('loadSpectrum');

var action=getAction() || {name:''};
var sample=API.cache('sample');
console.log("Receive action: ",action);

if (!sample) return;

if(this.action) {
    switch (action.name) {
    default:
        sample.handleAction(action);
    }    
} else if(this.variable) {
    await sample.handleDrop(this.variable, false, {
        autoJcamp: false
    });
    setTimeout( () => {
        API.doAction('selectRow', API.getData('mass').length-1);
    },0);
}

