if (this.event==='rowsSelected') {
    if (this.rows.length===1) {
        var item=this.rows[0].item;
        if (! item.accurate) {
            item.accurate={};
        }
    }
}

script:
console.log('Excecute action: ',this.action.name)

let action=this.action.name;
let value=this.action.value;
const emdb=API.cache('emdb');
switch (action) {
    case 'SpectrumInfo' :
        const jcampInfo = await API.require('vh/eln/util/jcampInfo');
        jcampInfo(this.action.value);
        break;
    case 'ShowGroups': {
        API.require('vh/chemistry/showMfGroupsList').then( showMfGroupsList => showMfGroupsList(API.cache('MolecularFormula')) );
        break;
    }
    case 'zoomChanged': {
            if (! emdb) return;
            let currentZoom=this.action.value;
            let previousZoom=API.cache('previousZoom') || {};
            if (previousZoom.from===currentZoom.from && previousZoom.to===currentZoom.to) return;
            API.cache('previousZoom',currentZoom)
            let experimental=emdb.experimentalSpectrum;
            if (! experimental) return;
            let bestPeaks=experimental.getBestPeaks( 
                {
                    from:value.from,
                    to:value.to
                }
            );
            API.createData('annotations', experimental.JsGraph.getPeaksAnnotation(bestPeaks));
        }
        break;
    case 'clearExperimentalSpectrum': {
        emdb.experimentalSpectrum=undefined;
        API.createData('experimentalChart', undefined);
        API.createData('kindAnnotations', undefined);
        API.createData('annotations', undefined);
        API.doAction('deselectRow', undefined);
        API.doAction('calculate', API.getData('preferences'));
    }
    case 'ExplodeSequence': {
        const MolecularFormula = API.cache('MolecularFormula');
        let preferences=API.getData('preferences');
        switch (String(preferences.sequenceKind)) {
            case 'peptidic':
                preferences.peptidic.sequence=MolecularFormula.Peptide.convertAASequence(String(preferences.peptidic.sequence))
                preferences.triggerChange();
                break;
            case 'nucleic':
                preferences.nucleic.sequence=MolecularFormula.Nucleotide.sequenceToMF(String(preferences.nucleic.sequence), preferences.nucleic.info.resurrect())
                preferences.triggerChange();
                break;
        }
        break;
    }
    case 'autoPeakWidth':
        let peaksWidth=API.cache('peaksWidth');
        let fct;
        if (peaksWidth) {
            fct = 'var width = '+ peaksWidth.A.toPrecision(5) + ' * mass ** '+peaksWidth.B.toPrecision(5) + ';\n';
            fct += 'return {bottom: massError + width * 3, top: massError + width}';
        } else {
            fct='';
        }
        let preferences=API.getData('preferences');
        preferences.mass.similarity.widthFunction=fct;
        preferences.triggerChange();
        break;
    case 'createSimilarityPeakWidth':
        if (value && value.ms && value.ms.similarity) {
            let similarity=value.ms.similarity;
            let em=value.ms.em;
            let annotations=[];
            let halfBottom=value.ms.similarity.width.bottom/2;
            let halfTop=value.ms.similarity.width.top/2;
            annotations.push({
                type:'rect',
                position: [
                    { x: em-halfTop, y: '5px' },
                    { x: em+halfTop, y: '10px'}
                ]
            });
            annotations.push({
                type:'rect',
                position: [
                    { x: em-halfBottom, y: '15px' },
                    { x: em+halfBottom, y: '20px'}
                ]
            });
            API.createData('widthAnnotation', annotations);
        }
        break;
    case 'ResetPreferences': {
            let preferences=API.getData('preferences');
            preferences.resetValue();
        }
        break;
}

