            API.doAction('save');
            break;
    }
}

if (typeof IframeBridge !== 'undefined') {
    this.showButton('save');   
}

console.log('Calculate annotations when fragments change')

const fragments=API.getData('fragments').slice(0);
fragments.sort( (a,b) => a.ms.em-b.ms.em);

// we group the fragments by experimental mass
let currentMSEM=Number.NaN;
const groups=[];
let currentGroup=[];
for (let fragment of fragments) {
    if (currentMSEM!==Number(fragment.ms.em)) {

        currentMSEM=Number(fragment.ms.em);
        currentGroup=[];
        groups.push(currentGroup);
    }
    currentGroup.push(fragment);
}
const annotations=[];

// fragments may have exactly the same mass. We need to join them

for (let group of groups) {
    let annotation={};
    for (let i=0; i<group.length; i++) {
        let fragment=group[i];
        fragment.color=getColor(fragment.comment,10);
        fragment.modification=fragment.parts.filter(part => part).join(' ');
        if (i===0) {
            annotation={
                position:[
                    {x:fragment.ms.em,y:"1px", dx:"-1px"},
                    {x:fragment.ms.em,y:(20+(group.length*12))+"px", dx:"0px"}
                ],
                type:"rect",
                fillColor: getColor(fragment.comment,fragment.ms.charge),
                strokeColor: getColor(fragment.comment,fragment.ms.charge),
                _highlight: fragment._highlight,
                info: fragment,
                label: []
            }
        }
        annotation.label.push(
            {
                text: fragment.comment,
                position: {
                    x:fragment.ms.em,
                    dx: "2px",
                    y: (10+(i*12))+"px"
                }
            }
        );
        if (i===(group.length-1)) {
            annotation.label.push(
                {
                    text: 'Z:'+fragment.ms.charge,
                    color:'grey',
                    position: {
                        x:fragment.ms.em,
                        dx: "2px",
                        y: (22+(i*12))+"px"
                    }
                }
            )
        }
    }
    annotations.push(annotation);
}

API.createData('kindAnnotations', annotations);


function getColor(annotation, charge) {
    if (! annotation) return;

    var alpha=1/(Math.abs(charge)||1);
    var type=annotation.replace('cmp-','').charAt(0);
    switch (type) {
        case 'a':
            return "rgba(255,0,255,"+alpha+")";
            break;
        case 'b':
            return "rgba(255,0,0,"+alpha+")";
            break;
        case 'c':
            return "rgba(255,255,0,"+alpha+")";
            break;
        case 'd':
            return "rgba(255,127,127,"+alpha+")";
            break;
        case 'w':
            return "rgba(127,127,255,"+alpha+")";
            break;
        case 'x':
            return "rgba(0,255,255,"+alpha+")";
            break;
        case 'y':
            return "rgba(0,255,0,"+alpha+")";
            break;
        case 'z':
            return "rgba(0,0,255,"+alpha+")";
            break;
    }
}

