script:
console.log('Calculate mass shift')

let prefs=API.getData('preferences').resurrect();
if (! prefs.mass.shift) prefs.mass.shift={};
console.log(prefs);

let emdb=API.cache('emdb');
if (!emdb.experimentalSpectrum) return;

if (this.button) {
    this.action={
        name:this.button
    }
}

console.log('Execute: ',this.button);
if (!prefs.mass.shift.minSimilarity) return;

let fragments=API.getData('fragments');

let regression;
console.log(prefs.mass)
try {
    regression=emdb.massShifts(fragments, {
        minSimilarity : (prefs.mass.shift.minSimilarity / 100) || 0.95,
        minLength : prefs.mass.shift.minLength || 10 
    });
} catch (e) {
    UI.showNotification(e.toString(),'warning');
    return;
}

switch (this.action.name) {
    case 'calculateXShiftFunction':
        API.createData('shifts', regression.shifts);
        API.createData('shiftsPPM', regression.shiftsPPM);
        API.createData('shiftsRegression', regression.fit);
        API.createData('shiftsRegressionTex', regression.tex);
        API.cache('shiftFunction', (mass) => regression.predictFct(mass) );
        API.doAction('UpdateAnalyseButtons', undefined);
        break;
    case 'calculateXNewShiftFunction':
        API.createData('shiftsCorrected', regression.shifts);
        API.createData('shiftsPPMCorrected', regression.shiftsPPM);
        API.createData('shiftsCorrectedRegression', regression.fit);
        API.createData('shiftsCorrectedRegressionTex', regression.tex);
        break;
}

let emdb=API.cache('emdb');
if (! emdb.experimentalSpectrum) return;

let preferences=API.getData('preferences').resurrect();

let fragments=API.getData('selectedFragments');
if (! fragments || fragments.length<2) {
    fragments=API.getData('fragments');
    API.createData('reportInfo','Schema use all the fragments because less than 2 were selected');
} else {
    API.createData('reportInfo','Schema using '+fragments.length+' fragments');
}

fragments=fragments.map( fragment => {
    return {
        type: String(fragment.comment),
        similarity: Math.round(fragment.ms.similarity.value*100),
        charge: Number(fragment.ms.charge)
    }
});
let parserOptions={};
let sequence='';

switch(preferences.sequenceKind) {
    case 'peptidic':
        sequence=preferences.peptidic.sequence;
        parserOptions.kind='peptide';
        break;
    case 'nucleic':
        parserOptions=preferences.nucleic.info;
        sequence=preferences.nucleic.sequence;
        break;
    default:
        return;
}

console.log(parserOptions);

const MolecularFormula=API.cache('MolecularFormula');

let options=JSON.parse(JSON.stringify(API.getData('svgPreferences')));
options.parsing=parserOptions;

let svgReport = MolecularFormula.Report.sequenceSVG(sequence, fragments, options);

