async function loadDemos() {
    let files=(await cdb.list()).filter( entry => entry.filename.match(/^upload/));
    files.forEach( file => {
        file.code=file.filename.replace(/.*\/(.*)\..*/,'$1');
        file.extension=file.filename.replace(/.*\.(.*)/,'$1');
    });
    files=files.filter( file => file.extension.match(/jdx|txt|json/));

    files.sort( (a,b) => (a.extension==='json') ? 1 : -1);
    const entries={};
    // 3 kind of files: .txt, .jdx and .json
    // .json will contain meta information as well as preferences
    // we need to group them !

    for (let file of files) {
        switch (file.extension) {
            case 'jdx':
                entries[file.code]={
                    filename: file.code,
                    jcamp: {
                        filename: 'spectra/mass/'+file.code,
                        dUrl: file.url,
                        data: {
                             type: 'jcamp',
                             url: file.url
                        }   
                    }
                }
                break;
            case 'txt':
                entries[file.code]={
                    filename: file.code,
                    text: {
                        filename: 'spectra/mass/'+file.code,
                        dUrl: file.url,
                        data: {
                             type: 'text',
                             url: file.url
                        }   
                    }
                }
                break;
            case 'json':
                if (! entries[file.code]) return;
                let content=await cdb.get(file.filename);
                entries[file.code].reference=content.reference;
                Object.assign( entries[file.code], content );
                break;
        }
    }

    let mass = API.getData('mass');
    mass.length=0;
    Object.keys(entries).forEach( key => mass.push(entries[key]));
    mass.sort( (a,b) => (a.filename<b.filename) ? -1 : 1);
    mass.triggerChange();
}

